import logo from './logo.svg';
import './App.css';

function App() {
  const Clock = () => {  
  const [seconds, updateSeconds] = React.useState(new Date().getSeconds());
  const [minutes, updateMinutes] = React.useState(new Date().getMinutes());
  const [hours, updateHours] = React.useState(new Date().getHours());
  const [value, updateValue] = React.useState(1);

  const savedCallback = React.useRef();

  const handleChange = (e) => {
    updateValue(e.target.value)
  }

  const callback = () => {
    updateSeconds(seconds - value);

    if(seconds <= 0){
      updateSeconds(60 - value);
      updateMinutes(minutes - 1);

      if(minutes <=0){
        updateMinutes(59);
        updateHours(hours - 1)

        if(hours <=0){
          updateHours(23)
        }
      }
    }
  }

  const tick = () => {
     savedCallback.current();
  }

  React.useEffect(() => {
    savedCallback.current = callback;
  })

  React.useEffect(() => {
    let tiktok = setInterval(tick, 1000);

    return () => tiktok.clear();
  }, [])


    return (
      <div>
        <h1> Seconds: { seconds }</h1>
        <h2> Minutes: { minutes }</h2>
        <h3> Hours: { hours }</h3>
        <input
          id="seconds"
          type="number"
          min="1"
          max="9"
          onChange={handleChange}
        />
      </div>
    )

}

ReactDOM.render(<Clock />, document.getElementById('clock'));
}

export default App;
